mapper.select:
SELECT COUNT(*) 
FROM FOLLOW
WHERE MEMBER_NO = #{memberNo}
AND FOLLOWER_NO = #{followerNo}

mapper.delete:
DELETE FROM FOLLOW
WHERE MEMBER_NO = #{memberNo}
AND FOLLOWER_NO = #{followerNo}

mapper.select:
SELECT PROFILE_IMG
FROM MEMBER_PROFILE
WHERE MEMBER_NO = #{sendNo}

SELECT MEMBER_NO 
FROM BOARD
WHERE BOARD_NO = #{boardNo}
